import React, { useState } from "react";
import { Button, Form } from "react-bootstrap";

const WorkerForm = () => {
  // State for storing form inputs
  const [formData, setFormData] = useState({
    firstname: "",
    middlename: "",
    lastname: "",
    idNumber: "",
    department: "",
    bankAccount: "",
  });

  // State for managing the success/error message
  const [message, setMessage] = useState("");

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission and send data to the backend API
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:4000/api/workers/", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        // If the request is successful, show a success message
        setMessage("Worker added successfully!");
      } else {
        // Handle errors by displaying a failure message
        setMessage("Failed to add worker. Please try again.");
      }
    } catch (error) {
      // Handle network or other errors
      setMessage("Error: Failed to connect to the server.");
    }
  };


  return (
    <div>
      <Form className="card p-4" onSubmit={handleSubmit}>
        <Form.Group className="mb-3" controlId="firstname">
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            name="firstname"
            value={formData.firstname}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="middlename">
          <Form.Label>Middle Name</Form.Label>
          <Form.Control
            type="text"
            name="middlename"
            value={formData.middlename}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="lastname">
          <Form.Label>Last Name</Form.Label>
          <Form.Control
            type="text"
            name="lastname"
            value={formData.lastname}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="idNumber">
          <Form.Label>ID Number</Form.Label>
          <Form.Control
            type="text"
            name="idNumber"
            value={formData.idNumber}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="department">
          <Form.Label>Department</Form.Label>
          <Form.Control
            type="text"
            name="department"
            value={formData.department}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Form.Group className="mb-3" controlId="bankAccount">
          <Form.Label>Bank Account</Form.Label>
          <Form.Control
            type="text"
            name="bankAccount"
            value={formData.bankAccount}
            onChange={handleChange}
            required
          />
        </Form.Group>

        <Button
          variant="primary"
          size="sm"
          type="submit"
          className="w-25 mx-auto mt-2 mb-3 d-block"
        >
          Submit
        </Button>
      </Form>

      {message && (
        <div
          style={{
            marginTop: "20px",
            color: message.includes("successfully") ? "green" : "red",
          }}
        >
          {message}
        </div>
      )}
    </div>
  );
};

export default WorkerForm;
