import React, { useState } from 'react';

const WorkerForm = () => {
  // State for storing form inputs
  const [formData, setFormData] = useState({
    firstname: '',
    middlename: '',
    lastname: '',
    idNumber: '',
    department: '',
    bankAccount: '',
  });

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission and send data to the backend API
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:4000/api/workers', {
        method: 'POST', // Use the appropriate HTTP method (POST for creating new data)
        headers: {
          'Content-Type': 'application/json', // We are sending JSON data
        },
        body: JSON.stringify(formData), // Send the form data as JSON
      });

      if (response.ok) {
        // If the request is successful, you can handle success
        console.log('Worker added successfully');
      } else {
        // Handle errors, e.g., if the API returns a non-200 status code
        console.error('Failed to add worker');
      }
    } catch (error) {
      // Handle network or other errors
      console.error('Error:', error);
    }
  };

  return (
    <form onSubmit={handleSubmit}>
      <div>
        <label htmlFor="firstname">First Name</label>
        <input
          type="text"
          id="firstname"
          name="firstname"
          value={formData.firstname}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label htmlFor="middlename">Middle Name</label>
        <input
          type="text"
          id="middlename"
          name="middlename"
          value={formData.middlename}
          onChange={handleChange}
        />
      </div>

      <div>
        <label htmlFor="lastname">Last Name</label>
        <input
          type="text"
          id="lastname"
          name="lastname"
          value={formData.lastname}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label htmlFor="idNumber">ID Number</label>
        <input
          type="text"
          id="idNumber"
          name="idNumber"
          value={formData.idNumber}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label htmlFor="department">Department</label>
        <input
          type="text"
          id="department"
          name="department"
          value={formData.department}
          onChange={handleChange}
          required
        />
      </div>

      <div>
        <label htmlFor="bankAccount">Bank Account</label>
        <input
          type="text"
          id="bankAccount"
          name="bankAccount"
          value={formData.bankAccount}
          onChange={handleChange}
          required
        />
      </div>

      <button type="submit">Submit</button>
    </form>
  );
};

export default WorkerForm;
