import React, { useState } from 'react';
import { Form, Button, Alert } from 'react-bootstrap';

const PayrollForm = () => {
  // State for storing form inputs
  const [formData, setFormData] = useState({
    payrollPeriod: '',
    payrollDate: '',
    grossAmount: '',
    netAmount: '',
    deductions: '',
    deductionAmount: '',
    totalAmount: '',
    workerId: '', // To associate the payroll with a specific worker
  });

  // State for managing success or error messages
  const [message, setMessage] = useState('');

  // Handle input changes
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Handle form submission and send data to the backend API
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('http://localhost:4000/api/payrolls', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setMessage(<Alert variant="success">Payroll added successfully!</Alert>);
      } else {
        const errorText = await response.text();
        setMessage(<Alert variant="danger">Failed to add payroll: {errorText}</Alert>);
      }
    } catch (error) {
      setMessage(<Alert variant="danger">Error: Failed to connect to the server. ${error.message}</Alert>);
    }
  };

  return (
    <div className='mt-4 pl-4'>
    <Form onSubmit={handleSubmit}>
      <Form.Group className="mb-2" controlId="payrollPeriod">
        <Form.Label>Payroll Period</Form.Label>
        <Form.Control
          type="text"
          name="payrollPeriod"
          value={formData.payrollPeriod}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="payrollDate">
        <Form.Label>Payroll Date</Form.Label>
        <Form.Control
          type="text"
          name="payrollDate"
          value={formData.payrollDate}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="grossAmount">
        <Form.Label>Gross Amount</Form.Label>
        <Form.Control
          type="number"
          name="grossAmount"
          value={formData.grossAmount}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="netAmount">
        <Form.Label>Net Amount</Form.Label>
        <Form.Control
          type="number"
          name="netAmount"
          value={formData.netAmount}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="deductions">
        <Form.Label>Deductions</Form.Label>
        <Form.Control
          type="text"
          name="deductions"
          value={formData.deductions}
          onChange={handleChange}
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="deductionAmount">
        <Form.Label>Deduction Amount</Form.Label>
        <Form.Control
          type="number"
          name="deductionAmount"
          value={formData.deductionAmount}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="totalAmount">
        <Form.Label>Total Amount</Form.Label>
        <Form.Control
          type="number"
          name="totalAmount"
          value={formData.totalAmount}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Form.Group className="mb-2" controlId="workerId">
        <Form.Label>Worker ID</Form.Label>
        <Form.Control
          type="number"
          name="workerId"
          value={formData.workerId}
          onChange={handleChange}
          required
        />
      </Form.Group>

      <Button variant="primary" type="submit">
        Submit
      </Button>

      {message && (
        <Alert style={{ marginTop: '20px' }}>
          {message}
        </Alert>
      )}
    </Form>
    </div>
  );
};

export default PayrollForm;
